import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import links, { deleteLink } from './features/links';
import { useReducer, useState } from 'react';
import {addLink} from './features/links';
import ReactPaginate from 'react-paginate';


function App() {
  const linkList = useSelector((state) => state.links.value);
  const [pageNumber, setPageNumber] = useState(0);

  return (
    <div className="App">
      {""}
      <div className='addLinks'>
        <a href='#'>Submit A Link</a>
      </div>
      <div className='linkList'>
        {linkList.map((link) => {
          return (
            <div className='linkBox'>
              <div className='upvotes'>
                {link.points}
              </div>
              <div className='info'>
                <h2> {link.name} </h2>
          <a href={link.url}>{link.url}</a>
          <button className='upVote'>Upvote</button>
          <button className='downVote'>Downvote</button>
              </div>
              <button className='deleteLink' onClick={() => {dispatch(deleteLink({id: link.id}));}} >Delete</button>
          </div>
          )

        })}

      </div>
    </div>
  );
}

export default App;
