import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import links, {addLink, deleteLink, linkSlice, updateVotes } from './features/links';
import { useEffect, useReducer, useState } from 'react';
import ReactPaginate from 'react-paginate';


function App() {

  const dispatch = useDispatch();
  const linkList = useSelector((state) => state.links.value);
  const [pageNumber, setPageNumber] = useState(0);
  const linksPerPage = 5;
  const firstLinkOfPage = pageNumber * linksPerPage;
  const pageCount = Math.ceil(linkList.length / linksPerPage);
  const changePage = ({selected}) => {
    setPageNumber(selected);
  }

  const displayLinks = linkList
  .sort((a,b) => a.id - b.id)
  .slice(firstLinkOfPage, firstLinkOfPage + linksPerPage)
  .map((link) => {
    return (
      <div className='linkBox'>
        <div className='upvotes'>
          {link.points}
        </div>
        <div className='info'>
          <h2> {link.name} </h2>
    <a href={link.url}>{link.url}</a>
    <button className='upVote' onClick={() => {
      dispatch(updateVotes({id: link.id, point: 1, buttonName: "upvote"}))
    }}>
      Upvote
    </button>
    <button className='downVote' onClick={() => {
      dispatch(updateVotes({id: link.id, point: -1, buttonName: "downvote"}))
    }}>
      Downvote
    </button>
        </div>
        <button className='deleteLink' onClick={() => {
          dispatch(deleteLink({id: link.id}))
        } }>
          Delete
          </button>
    </div>
    )
  });

  return (
    <div className="App">
      {""}
      <div className='addLinks'>
        <a href='#'>Submit A Link</a>
      </div>
      <div className='linkList'>
        {displayLinks}
      </div>
      <ReactPaginate 
      previousLabel={'Önceki'}
      nextLabel={'Sonraki'}
      pageCount= {pageCount}
      onPageChange={changePage}
      containerClassName={'paginationContainer'}
      previousLinkClassName={'prevBtn'}
      nextLinkClassName={'prevBtn'}
      disabledClassName={'pgntnDisabled'}
      activeClassName={'pgntnActive'}
      />
    </div>
  );
}

export default App;
