import React from 'react';
import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import links, {addLink, deleteLink, linkSlice, updateVotes, sortLinks } from './features/links';
import { useEffect, useReducer, useState } from 'react';
import { useNavigate } from "react-router-dom";
import { Link } from 'react-router-dom';
import Popup from 'reactjs-popup'
import 'reactjs-popup/dist/index.css';

const Postlink = () => {

    const dispatch = useDispatch();
    const linkList = useSelector((state) => state.links.value);
    const [linkName, setLinkName] = useState("");
    const [linkUrl, setLinkUrl] = useState("");

    const SuccesNotice = () => {
        const [open, setOpen] = useState(false);
        const closeToolTip = () => setOpen(false);
        return(
            <Popup open={open} onClose={closeToolTip}>
            <p>Link submitted</p>
        </Popup>
        ) 
    }
    
    return(
        <div>
        <Link to="/">Return to list</Link>
        <div className='postLinkContent'>
            <h2>Add new link</h2>
            <label>Link Name</label>
            <input type={'text'} placeholder="e.g. Alphabet" onChange={event => setLinkName(event.target.value)} />
            <label>Link Name</label>
            <input type={'url'} placeholder="e.g. http://abc.xyz" onChange={event => setLinkUrl(event.target.value)} />
            <button onClick={() => {
                dispatch(addLink({id: linkList[linkList.length - 1].id + 1, name: linkName, url: linkUrl, points: 0}))
            }}>Submit</button>
        </div>
        </div>
    )
    
}

export default Postlink;
