import './App.css';
import { useSelector, useDispatch } from 'react-redux';
import links, {addLink, deleteLink, linkSlice, updateVotes, sortLinks } from './features/links';
import { useEffect, useReducer, useState } from 'react';
import ReactPaginate from 'react-paginate';
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';
import Select from 'react-select'


function App() {

  const dispatch = useDispatch();
  const linkList = useSelector((state) => state.links.value);
  const [pageNumber, setPageNumber] = useState(0);
  const linksPerPage = 5;
  const firstLinkOfPage = pageNumber * linksPerPage;
  const pageCount = Math.ceil(linkList.length / linksPerPage);
  const changePage = ({selected}) => {
    setPageNumber(selected);
  }

  const orderType = 'mostLiked';

  useEffect(() => {
    dispatch(sortLinks({linkList, orderType}))
  }, [orderType, linkList]);

  let navigate = useNavigate();
  function sendToPostLink() {
    navigate("/Postlink");
  }

  const SortSelect = () => {
    const options = [
      { value: 'mostLiked', label: 'Most Upvoted' },
      { value: 'leastLiked', label: 'Least Upvoted' }
    ];

    return(
      <div>
        <Select options={options} />
        <p>asdasdsa</p>
      </div>
    )
  }

  const displayLinks = linkList.slice(firstLinkOfPage, firstLinkOfPage + linksPerPage).map((link) => {
    return (
      <div className='linkBox'>
        <div className='upvotes'>
          {link.points} -- {link.id}
        </div>
        <div className='info'>
          <h2> {link.name} </h2>
    <a href={link.url}>{link.url}</a>
    <button className='upVote' onClick={() => {
      dispatch(updateVotes({id: link.id, point: 1, buttonName: "upvote"}))
    }}>
      Upvote
    </button>
    <button className='downVote' onClick={() => {
      dispatch(updateVotes({id: link.id, point: -1, buttonName: "downvote"}))
    }}>
      Downvote
    </button>
        </div>
          <Popup trigger={<button className='deleteButton'> Click to open popup </button>} 
          modal 
          lockScroll 
          closeOnDocumentClick 
          repositionOnResize 
          className="deleteBox">
      <button className='deleteLink' onClick={() => {
          dispatch(deleteLink({id: link.id}))
        }}>
          Delete
          </button>
    </Popup> 
    </div>
    )
  });

  return (
    <div className="App">
      {""}
      <div className='addLinks'>
      <button onClick={sendToPostLink}>Submit A Link</button>
      </div>
      {SortSelect}
      <div className='linkList'>
        {displayLinks}
      </div>
      <ReactPaginate 
      previousLabel={'Önceki'}
      nextLabel={'Sonraki'}
      pageCount= {pageCount}
      onPageChange={changePage}
      containerClassName={'paginationContainer'}
      previousLinkClassName={'prevBtn'}
      nextLinkClassName={'prevBtn'}
      disabledClassName={'pgntnDisabled'}
      activeClassName={'pgntnActive'}
      />
    </div>
  );
}

export default App;
